import logo from "./logo.svg";
import React, { useState, useEffect } from "react";
import ChatList from "./chatApp/ChatList";

import "./App.css";

function App() {
  const [chats, setChats] = useState([]);
  const [selectedChat, setSelectedChat] = useState(null);

  const handleSelectChat = (chatId) => {
    setSelectedChat(chatId);
    const updatedChats = chats.map((chat) => ({
      ...chat,
      selected: chat.id === chatId ? true : false,
    }));
    setChats(updatedChats);
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          "https://my-json-server.typicode.com/codebuds-fk/chat/chats"
        );
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const data = await response.json();
        console.log("response", data)
        // Add selected property to each chat object for highlighting
        const chatsWithSelection = data.map((chat) => ({
          ...chat,
          selected: false,
        }));
        setChats(chatsWithSelection);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="App">
      <ChatList chats={chats} onSelectChat={handleSelectChat} />
    </div>
  );
}

export default App;
